questionvariables.text:
/* Variant 1: RL Step Response */
Vs: rand([10, 12, 15, 20]);
R: rand([100, 200, 500, 1000]);
L: rand([0.01, 0.02, 0.05, 0.1]);

/* Computed quantities */
tau: L/R;
Isteady: Vs/R;

/* Transfer function H(s) = I(s)/V(s) = 1/(R + s*L) */
Hs: 1/(R + s*L);

/* Part A correct answer */
correctA: a;

/* Part B correct answer */
correctB: 1/(R + s*L);

/* Part C correct answer */
correctC: tau;

/* Variant 2: RC Step Response */
Vs: rand([5, 10, 12, 15]);
R: rand([1000, 2200, 3300, 4700]);
C: rand([10*10^(-6), 22*10^(-6), 47*10^(-6), 100*10^(-6)]);

/* Computed quantities */
tau: R*C;

/* Transfer function H(s) = Vc(s)/Vs(s) = 1/(1 + s*R*C) */
correctB: 1/(1 + s*R*C);

/* Part A correct answer */
correctA: b;

/* Part C correct answer */
correctC: tau;

/* Variant 3: RL Natural Response */
I0: rand([0.5, 1, 2, 3, 5]);
R: rand([50, 100, 200, 500]);
L: rand([0.01, 0.02, 0.05, 0.1]);

/* Computed quantities */
tau: L/R;
W0: (1/2)*L*I0^2;

/* I(s) = I0/(s + R/L) = L*I0/(s*L + R) */
correctB: I0/(s + R/L);

/* Part A correct answer */
correctA: b;

/* Part C correct answer */
correctC: tau;

/* Variant 4: RC Natural Response */
V0: rand([5, 10, 12, 15, 20]);
R: rand([1000, 2200, 4700, 10000]);
C: rand([10*10^(-6), 22*10^(-6), 47*10^(-6), 100*10^(-6)]);

/* Computed quantities */
tau: R*C;
W0: (1/2)*C*V0^2;

/* Vc(s) = V0/(s + 1/(R*C)) */
correctB: V0/(s + 1/(R*C));

/* Part A correct answer */
correctA: b;

/* Part C correct answer */
correctC: tau;
