questionvariables.text:
/* RL Step with pi-network */
Vs: rand([8, 10, 12, 15]);
R1: rand([200, 330, 470, 680]);
R2: rand([100, 220, 470, 1000]);
R3: rand([100, 150, 220, 330]);
L: rand([0.02, 0.05, 0.1, 0.2]);

/* Computed quantities */
R1parR2: R1*R2/(R1+R2);
Req: R3 + R1parR2;
tau: L/Req;
Vth: Vs*R2/(R1+R2);
Isteady: Vth/Req;

/* Answers */
correctA: a;
correctB: Req;
correctC: tau;
taD: "See model answer in general feedback.";

feedbackvariables.text:
/* Order-of-magnitude check for tiered grading */
_ratio: if (correctB = 0 or ansB = 0) then 0 else float(abs(ansB / correctB));
_log10r: if _ratio > 0 then float(log(_ratio)/log(10)) else -999;
_nearint: round(_log10r);
_mag_err: is(_ratio > 0 and abs(_nearint) >= 1 and abs(_log10r - _nearint) < 0.2);

/* Order-of-magnitude check for tiered grading */
_c_ratio: if (correctC = 0 or ansC = 0) then 0 else float(abs(ansC / correctC));
_c_log10r: if _c_ratio > 0 then float(log(_c_ratio)/log(10)) else -999;
_c_nearint: round(_c_log10r);
_c_mag_err: is(_c_ratio > 0 and abs(_c_nearint) >= 1 and abs(_c_log10r - _c_nearint) < 0.2);

