questionvariables.text:
/* Q4-V2: RL with Switch */
Vs: rand([12,15,20,24]);
R1: rand([100,200,330,470]);
R2: rand([200,330,470,680]);
Lval: rand([10,20,47,100]);

/* Initial condition: L is short at DC, so I0 = Vs/R1 */
I0: float(Vs / R1);
